package JavaEE.modelViewController;

/**
 * 创建模型。
 * 
 * @author MJCoder
 */
public class Student {
	private String rollNo; // roll：卷；滚动，转动；辗；起伏，摇晃；卷形物；名单；摇晃；牛肠；面包卷
	private String name;

	/**
	 * 
	 */
	public Student() {
		// TODO Auto-generated constructor stub
	}

	/**
	 * @return the rollNo
	 */
	public String getRollNo() {
		return rollNo;
	}

	/**
	 * @param rollNo
	 *            the rollNo to set
	 */
	public void setRollNo(String rollNo) {
		this.rollNo = rollNo;
	}

	/**
	 * @return the name
	 */
	public String getName() {
		return name;
	}

	/**
	 * @param name
	 *            the name to set
	 */
	public void setName(String name) {
		this.name = name;
	}

}
